import Syllabus from './Syllabus';
import SyllabusContext from './../../../context/syllabusContext';

class Syllabi extends Component {
  constructor(props) {
    super(props);
  }
  state = {
    clickToAddDelete: null
  };
  static contextType = SyllabusContext;
