  // componentWillUnmount() {
  //   console.log('[Tasks.js] componentWillUnmount');
  // }

  render() {
    //alert('inside syllabi render()');
    console.log('[allSyllabus] rendering...');
    //<p> contentChoice: {this.props.everything.contentChoice}</p>
