      <div>
        <h3 id="title"> Syllabi</h3>

        <table id="students">
          <tbody>
            <tr>{this.renderAllSyllabiTableHeaderHandler()}</tr>
            {this.allSyllabiHandler()}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Syllabi;
