import React, { Component } from 'react';
import DatePickerPicker from '../../containers/RightCockpit/DatePicker.js';
//import ReactDatePicker from '../Calendar/ReactDatePicker';
import Icon from '../Calendar/Icon';
import { Form, Input, FormGroup, Container, Label } from 'reactstrap';
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';
import { SingleDatePicker } from 'react-dates';

// onSubmit = e => {
// e.preventDefault();

// const [newEvent, setNewEvent] = useState({
//   title: ''
// });

// setNewEvent({
//   [e.target.name]: e.target.value
// });
// let bigEvent = { [e.target.name]: e.target.value };
// props.newestEvent(bigEvent);
// };

class NewEvent extends Component {
  state = {
    eventTitle: '',
    day: '',
    month: '',
    year: '',
    time: '',
    date: null,
    focused: null
  };
  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    this.props.newestEvent(this.state);
  };

  render() {
    let showCaseReactDate = (
      <div>
        <Container>
          <Form>
            <FormGroup>
              <Label for="firstname">Name: </Label>
              <Input type="text" name="firstname" />
            </FormGroup>
            <FormGroup>
              <Label for="lastname">Last Name: </Label>
              <Input type="text" name="lastname" />
            </FormGroup>
            <FormGroup>
              <SingleDatePicker
                // showClearDate={true}
                customInputIcon={
                  <svg className="icon icon-small">
                    <Icon icon="ICON_CALENDER" className="icon icon-large" />
                  </svg>
                }
                inputIconPosition="after"
                small={true}
                block={false}
                numberOfMonths={1}
                date={this.state.date}
                onDateChange={date => this.handleDateChange(date)}
                focused={this.state.focused}
                onFocusChange={({ focused }) => this.setState({ focused })}
                openDirection="up"
                hideKeyboardShortcutsPanel={true}
              />
            </FormGroup>
          </Form>
        </Container>
      </div>
    );
    return (
      <React.Fragment>
        <div className="container">
          <form>
            <div className="form-group">
              <label>Event Title</label>
              <input
                type="text"
                name="title"
                className="form-control"
                value={this.state.title}
                onChange={e => this.change(e)}
              />
            </div>

            <div className="form-group">
              <label>Event Name</label>
              <input
                type="text"
                name="name"
                className="form-control"
                value={this.state.name}
                onChange={e => this.change(e)}
              />
            </div>

            <button onClick={e => this.onSubmit(e)}>Submit</button>
          </form>
        </div>

        {showCaseReactDate}
      </React.Fragment>
    );
  }
}

export default NewEvent;

