  // {
  //   "name": "State",
  //   "value": "Wed Jan 08 2020 19:45:00 GMT-0500 (Colombia Standard Time)",
  //   "subHooks": []
  // }

  const startDateTime = (
    <React.Fragment>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Select Date: </label>
          <DatePicker
            selected={startDate}
            onChange={date => startDateTimeHandler(date)}
            name="startDate"
            dateFormat="MM/dd/yyyy"
            locale="pt-BR"
            showTimeSelect
            timeFormat="p"
            timeIntervals={15}
            timeCaption="Start"
          />
        </div>

        <div className="form-group">
          <button className="btn btn-success">Add Date</button>
        </div>
      </form>
    </React.Fragment>
  );
  let showStartDate = null;
  if (startDate) {
    showStartDate = { startDate };
  }
  // const endTime = (
  //   <DatePicker
  //     selected={startDate}
  //     onChange={startDate => finishTimeHandler(startDate)}
  //     showTimeSelect
  //     timeFormat="HH:mm"
  //     timeIntervals={15}
