  //     dateFormat="h:mm aa"
  //   />
  // );
  //{startDateTime}
  // <div>{finishTime ? <div>{finishTime}</div> : null}</div>
  return (
    <React.Fragment>
      <div>{startDateTime}</div>
    </React.Fragment>
  );
};

