import React from 'react';
import classes from '../../components/Cockpit/Cockpit.module.css';
import Cockpit from '../../components/Cockpit/Cockpit';
import Cockpit2 from '../../components/Cockpit/Cockpit2';
import Navbar2 from '../../components/Cockpit/Navbar/Navbar2';
import Tasks from '../../components/Tasks/Tasks';
import TodayTasks from '../../components/TodayTasks/TodayTasks';
import Lessons from '../../components/Syllabus/MaxReact/Lessons';
import ViewContent from './ViewContent';
import NewTask from '../../components/Creation/newTask';

const displayContent = props => {
  contentViewHandler = event => {
    let newViewChoice = event.target.value;
    console.log(newViewChoice);
    //Comparing new contentChoice with previous contentChoice
    //if newContentChoice === oldContentChoice
    //turn off the view
    if (newViewChoice === this.state.contentChoice) {
      return this.setState({ contentChoice: '0' });
    } else {
      //Setting which Content view to show
      //there will be only 1 contentChoice stored in state
      switch (newViewChoice) {
        case '0': //only cockpit
          this.setState({ contentChoice: '0' });
          break;

        case '1': //All tasks
          this.setState({ contentChoice: '1' });
          if (this.state.tasks != 0) {
            this.setState({ lastHeader: this.state.tasks[0] });
          } else {
            this.setState({ lastHeader: this.state.lastHeader });
          }
          break;

        case '2': //TodaysTasks
          this.setState({ contentChoice: '2' });
          if (this.state.Monday != 0) {
            this.setState({ lastTodayTasksHeader: this.state.Monday[0] });
          } else {
            this.setState({
              lastTodayTasksHeader: this.state.lastTodayTasksHeader
            });
          }
          break;

        case '3': //Syllabus
          this.setState({ contentChoice: '3' });
          console.log(
            `this is lastLessonHeader ${this.state.lastLessonHeader}`
          );
          if (this.state.maxReact.length != 0) {
            this.setState({ lastLessonHeader: this.state.maxReact[0] });
          } else {
            this.setState({ lastLessonHeader: this.state.lastLessonHeader });
          }
          console.log(
            `this is lastLessonHeader ${this.state.lastLessonHeader}`
          );
          break;
        case '4': //New Task
          this.setState({ contentChoice: '4' });
          break;
      }
    }
  };

  let displayContent = null;

  switch (props.everything.contentChoice) {
    case '0':
      displayContent = (
        <React.Fragment>
          <h3>Hello Thomas.... What would you like to see?</h3>
          <img
            className={classes.logoImage}
            src={require('../../Assets/cockpitIcon.png')}
          />
        </React.Fragment>
      );
      break;

    case '1':
      displayContent = (
        <React.Fragment>
          <p>tasks has # {props.everything.tasks.length}</p>
                      
          <Tasks
            reRenderTasks={props.everything.reRenderTasks}
            tasks={props.everything.tasks}
            clicked={props.deleteTaskhandler}
            changed={props.taskChangeHandler}
            lastHeader={props.everything.lastHeader}
          />
                    
        </React.Fragment>
      );
      break;

    case '2':
      displayContent = (
        <React.Fragment>
                      
          <TodayTasks
            reRenderTodayTasks={props.everything.reRenderTodayTasks}
            monday={props.everything.Monday}
            clicked={props.deleteTodayTaskhandler}
            changed={props.todayTaskChangeHandler}
            lastTodayTasksHeader={props.everything.lastTodayTasksHeader}
          />
                    
        </React.Fragment>
      );
      break;

    case '3':
      displayContent = (
        <React.Fragment>
          <p>tasks has # {props.everything.maxReact.length}</p>
          <Lessons
            reRender={props.everything.showLessons}
            lessons={props.everything.maxReact}
            clicked={props.deleteLessonhandler}
            changed={props.lessonChangeHandler}
            lastLessonHeader={props.everything.lastLessonHeader}
            lessonsLength={props.everything.maxReact.length}
          />
        </React.Fragment>
      );
      break;
    case '4':
      displayContent = (
        <React.Fragment>
          <NewTask
            // newTaskTitle = {}
            // category = {}
            // location = {}
            // newTaskTitle={props.setState.newTaskTitle}
            newTaskLocation={event => props.newTaskLocationHandler(event)}
            newTaskInfo={event => {
              props.newTaskHandler(event);
            }}
          />
        </React.Fragment>
      );
      break;
  }

  return <div>{displayContent}</div>;
};

export default displayContent;
