          this.context.resetHandlerChoice(this.showHowBusyEverydayHandler());

          break;
      }
    }

    return (
      <React.Fragment>
        {this.context.dataRequestDetails['handlerChoice'] ? (
          <div>{this.context.dataRequestDetails.handlerChoice} </div>
        ) : null}
      </React.Fragment>
    );
  }
}

export default TasksData;
