// import moment from 'moment';
import React, { Component } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { compareAsc, format } from 'date-fns';

class DatePickerPicker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startDate: new Date()
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(date) {
    this.setState(
      {
        startDate: date
      },
      () => this.props.startDateTimeHandler(this.state.startDate)
    );
    console.log(`inside handleChange datePicker ${this.state.startDate}`);
  }

  handleChange2(date) {
    this.setState(
      {
        startDate2: date
      },
      () => this.props.startDateTimeHandler(this.state.startDate)
    );
    console.log(`inside handleChange2 datePicker ${this.state.startDate}`);
  }

  handleSubmit(e) {
    e.preventDefault();
    let main = this.state.startDate;
    alert(main);
    //console.log(main.format('L'));
  }
  //name="startDate"
  //dateFormat="MM/dd/yyyy"
  //dateFormat="PP"
  //locale="p-BR"
  //timeFormat="p"

  render() {
    let showDate1 = null;
    if (this.state.startDate) {
      showDate1 = <div>showDate1: {this.state.startDate}</div>;
    }
    let showDate2 = null;
    if (this.state.startDate2) {
      showDate2 = <div>showDate2: {this.state.startDate}</div>;
    }
    return (
      <React.Fragment>
        <div className="container">
          {showDate1}
          {showDate2}
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Select Event Date & Time: </label>
              <DatePicker
                placeholderText="Choose Start Time"
                //selected={this.state.startDate}
                onChange={date => this.handleChange(date)}
                dateFormat="MMMM d, yyyy h:mm aa"
                showTimeSelect
                timeFormat="HH:mm"
                timeIntervals={15}
                timeCaption="Start"
              />

              <DatePicker
                placeholderText="Choose End Time"
                selected={this.state.startDate2}
                onChange={date => this.handleChange2(date)}
                showTimeSelect
                timeFormat="HH:mm"
                timeIntervals={15}
                timeCaption="End"
                dateFormat="MMMM d, yyyy h:mm aa"
              />
            </div>
            <div className="form-group">
              <button className="btn btn-success">Add Date</button>
            </div>
          </form>
        </div>
      </React.Fragment>
    );
  }
}

export default DatePickerPicker;
