import React from 'react'
import './Task.css'
import Radium from 'radium';
import wtf from '../SpecialBtn.module.css'

const task = (props) => {

const querrystyle = {
    '@media (min-width: 500px)': {
        width: '450px'
    }
}
    const inputStyle = {
        ':focus' : { 
          backgroundColor: 'yellow'
        }
    }
  /*
const hiddenInput = {
    display: 'none',
    backgroundColor: 'yellow',
    padding: '20px',

    }

const showHiddenInput = { 
    ':hover': {
        display: 'block',
        color: 'purple'
        }
    }
*/


//onmouseenter = 'props.mouseover'
    return (
        <div className = 'Task' style = {querrystyle}>
        <div className = 'TaskDeadlineSep'>
            <p className = 'TaskDeadline' >Task: {props.todo}</p>
            <p className = 'TaskDeadline' >Deadline:  {props.deadline}</p>
            <p className = {wtf.showTaskDetails}>Location: {props.location}</p>    
            <p className = 'TaskDeadline delete' onClick = {props.click}>Delete Task</p>

            <div>
            <p className = {wtf.showTaskDetails2}>Hover to show details</p>
            </div>




        </div>


        <p>{props.children}</p>

        <input style = {inputStyle} type= 'text' onChange = {props.changed} value={props.todo}/>
        </div>
    )
    }
export default Radium(task)
