import React, { Component } from 'react';
import ShowAllTasksAfterAddingTask from '../../context/newTask-context';
import RightCockpit from '../../containers/RightCockpit/RightCockpit';
import DisplayContent from '../Cockpit/displayContent';
import TryingOutContext from '../../context/tryOutContext';

class NewSyllabus extends Component {
  constructor(props) {
    super(props);
  }

  state = {
    contentChoice2: '',
    showRightCockpit: false,
    contentChoice: '',
    contentChoice3: '4',
    myNum: '',
    character: 'Uncle Drew',
    showCharacter: false
  };

  change = e => {
    let name = e.target.name;

    let newValue = e.target.value;
    console.log(`I'm in change() in newSyllabus newValue:  ${newValue}`);
    this.setState({ contentChoice2: newValue });
    this.setState({ contentChoice3: newValue });
    console.log(JSON.stringify(newValue, null, 2));

    console.log(`this is newValue: ${newValue}`);
    console.log(
      `this is this.state.contentChoice2: ${this.state.contentChoice2}`
    );
  };

  onSubmit = e => {
    e.preventDefault();
    this.setState({ showRightCockpit: !this.state.showRightCockpit });
    console.log(`I'm in onSubmit ${this.state.contentChoice2}`);

    //passing new content choice to displayContent -> RightCockpit

    this.props.newestSyllabus(this.state.contentChoice2);

    console.log(`after resetting state ${this.state.contentChoice2}`);
  };

  contentChoiceHandler = () => {
    console.log('hey i am inside contentChoiceHandler');
    this.setState({ showRightCockpit: true });
    let showRightCockpitStatus = this.state.showRightCockpit;
    this.setState({ showRightCockpit: !showRightCockpitStatus });
    this.setState({ myNum: '11' });
    return <div>The myNum is {this.state.myNum}</div>;
  };
  static contextType = TryingOutContext;
  showCharacterHandler = () => {
    let currentShowStatus = this.state.showCharacter;
    this.setState({ showCharacter: !currentShowStatus });
  };
  render() {
    /*<TryingOutContext.Consumer>
          {context => context.realNum}
        </TryingOutContext.Consumer>
        */

    return (
      <div>
        <ShowAllTasksAfterAddingTask.Provider
          value={{
            character: this.state.character,
            showCharacter: this.state.showCharacter,
            showCharacterHandlerHandler: this.state.showCharacterHandler
          }}
        >
          <RightCockpit></RightCockpit>
        </ShowAllTasksAfterAddingTask.Provider>

        <p>{this.context.realNum}</p>
        <p>hello</p>
        {this.state.contentChoice2}
        <div className="container">
          <button onClick={this.context.contentChoiceHandler2}>
            Click to Add 1
          </button>
          <form onSubmit={e => this.onSubmit(e)}>
            <div className="form-group">
              <label>Syllabus</label>
              <textarea
                className="form-control form-control-sm"
                type="text"
                name="contentChoice"
                placeholder="Enter new Syllabus."
                onChange={e => this.change(e)}
              />
            </div>

            <button
              onClick={e => {
                this.contentChoiceHandler(e);
              }}
              type="submit"
              value="Submit"
            >
              {' '}
              Submit
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default NewSyllabus;
