import React, { Component } from 'react';
import DatePickerPicker from '../../containers/RightCockpit/DatePicker.js';
//import ReactDatePicker from '../Calendar/ReactDatePicker';
import Icon from '../Calendar/Icon';
import { Form, Input, FormGroup, Container, Label } from 'reactstrap';
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';
import {
  DateRangePicker,
  SingleDatePicker,
  DayPickerRangeController
} from 'react-dates';

// onSubmit = e => {
// e.preventDefault();

// const [newEvent, setNewEvent] = useState({
//   title: ''
// });

// setNewEvent({
//   [e.target.name]: e.target.value
// });
// let bigEvent = { [e.target.name]: e.target.value };
// props.newestEvent(bigEvent);
// };

class NewEvent extends Component {
  state = {
    eventTitle: '',
    day: '',
    month: '',
    year: '',
    time: '',
    date: null,
    focused: null,
    showPickedDate: false
  };
  change = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    this.props.newestEvent(this.state);
  };
  handleDateChange = event => {
    let date = event.target.value;
    this.setState({ date: date, showPickedDate: true });
  };
  render() {
    let showCaseReactDate = (
      <div>
        <Container>
          <Form>
            <FormGroup>
              <SingleDatePicker
                showClearDate={false}
                small={true}
                block={false}
                numberOfMonths={3}
                date={this.state.date}
                onDateChange={date => this.handleDateChange(date)}
                focused={this.state.focused}
                onFocusChange={({ focused }) => this.setState({ focused })}
                openDirection="down"
                hideKeyboardShortcutsPanel={true}
              />
            </FormGroup>
          </Form>
        </Container>
      </div>
    );

    let showCurrentPickedDate = <div>{JSON.stringify(this.state.date)}</div>;
    return (
      <React.Fragment>
        <div className="container">
          <form>
            <div className="form-group">
              <label>Event Title</label>
              <input
                type="text"
                name="title"
                className="form-control"
                value={this.state.title}
                onChange={e => this.change(e)}
              />
            </div>

            <div className="form-group">
              <label>Event Name</label>
              <input
                type="text"
                name="name"
                className="form-control"
                value={this.state.name}
                onChange={e => this.change(e)}
              />

              {showCaseReactDate}
            </div>

            <button onClick={e => this.onSubmit(e)}>Submit</button>
          </form>
        </div>
      </React.Fragment>
    );
  }
}
// {this.state.showPickedDate ? { showCurrentPickedDate } : null}
export default NewEvent;

