import React, { useEffect } from 'react';
//import rocky from '../../containers/App.module.css'
import classNames from 'classnames';
import classes from './Cockpit.module.css';

const Cockpit = props => {
  useEffect(() => {
    //can do anything that componentDidUpdate can do
    //can send http request here
    console.log('[Cockpit.js] useffect');

    //getting rid of timer
    const timer = setTimeout(() => {
      alert('Saved data to cloud');
    }, 1000);

    return () => {
      clearTimeout(timer);
      console.log('[Cockpit.js] cleanup work in useEffect');
    };
  });

  /*
    setTimeout(() => {
      alert('Saved data to cloud')
    }, 1000);
    return () => { //don't have to have a return statement
      console.log('[Cockpit.js] cleanup work in useEffect')
    }
  }, []); 
  */
  //if 2nd argument [props.tasks] it doesn't involve tasks -> it won't run this again
  //[props.tasks]

  useEffect(() => {
    console.log('[Cockpit.js] 2nd useEffect');
    return () => {
      console.log('[Cockpit.js] cleanup work in 2nd UseEffect');
    };
  });

  //styling the 'Things to Do' using strings for classnames
  const assignedClasses = [];
  let btnClass = '';

  switch (props.tasksLength) {
    case 3:
      assignedClasses.push('bold', 'red');
      break;
    case 2:
      assignedClasses.push('red');
      break;
    case 1:
      assignedClasses.push('orange');
      break;
    default:
      assignedClasses.push('green');
      break;
  }

  //console.log(assignedClasses[0])
  //add multiple classes for <p> w/ strings
  //const classesFinal = classes.join(' ')
  const classesFinal = 'rocky.' + assignedClasses;

  //console.log(classesFinal)

  //console.log(`This is classesFinal ${classesFinal}`)
  const cockpitBootstrap = (
    <div class="container">
      <div class="row">
        <div class="col-md-6">
          <div class="dropdown">
            <button
              class="btn btn-primary btn-block dropdown-toggle"
              type="button"
              data-toggle="dropdown"
            >
              2: View Options
            </button>
            <div class="dropdown-menu">
              <a class="dropdown-item" href="2_2_basic_typography.html">
                All Tasks
              </a>
              <a class="dropdown-item" href="2_3_text_alignment_display.html">
                Tasks Today
              </a>
            </div>
          </div>
        </div>

        <div class="col-md-6">
          <div class="dropdown">
            <button
              class="btn btn-success btn-block dropdown-toggle"
              type="button"
              data-toggle="dropdown"
            >
              Task Creation
            </button>
            <div class="dropdown-menu">
              <a class="dropdown-item" href="3_2_buttons.html">
                Syllabus
              </a>
              <a class="dropdown-item" href="3_3_navbar.html">
                Errands
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className={classes.Cockpit}>
      {cockpitBootstrap}
      <h1>{props.title}</h1>
      <p
        className={classNames({
          [classes[assignedClasses[0]]]: true,
          [classes[assignedClasses[1]]]: true
        })}
      >
        Things to Do!
      </p>

      <button className={classes.specialbtn} onClick={props.clicked}>
        Show Task
      </button>

      <button onClick={props.todayClicked}>Show Today Schedule</button>
    </div>
  );
};
//export default Cockpit;

export default React.memo(Cockpit);

