import React, { useContext } from 'react';
import classNames from 'classnames';
import classes from './Cockpit.module.css';
// //import rocky from '../../containers/App.module.css'
// import classNames from 'classnames';
// import classes from './Cockpit.module.css';
// import Navbar from './navBar';
import SyllabiList from '../ViewContent/Syllabus/SyllabiList';
import TasksContext from '../../context/tasksContext';
import SyllabusContext from '../../context/syllabusContext';

const ViewContentOptions = props => {
  const tasksContext = useContext(TasksContext);
  const syllabusContext = useContext(SyllabusContext);
  const pickedSyllabusRequestHandler = event => {
    //let dayPicked = event.target.value;
    //event
    let dataRequestMessage = {
      typeOfData: 'syllabus',
      handlerChoice: '3',
      dataLocation: null,
      infoType: 'triggerShowSyllabus',
      info: ''
    };
    //tasksContext.contentViewHandler(3)

    tasksContext.dataRequestHandler(event, dataRequestMessage);
  };

  const processSyllabiList = () => {
    return Object.keys(
      syllabusContext.everythingSyllabus.syllabusData.syllabi
    ).map((syllabus, index) => {
      return (
        <SyllabiList
          syllabusEverything={syllabusContext.everythingSyllabus.syllabusData}
          syllabusId={
            syllabusContext.everythingSyllabus.syllabusData[syllabus].syllabusId
          }
          index={index}
          syllabusName={
            syllabusContext.everythingSyllabus.syllabusData[syllabus]
              .syllabusName
          }
        />
      );
    });
  };

  const pickedDayRequestHandler = event => {
    let dayPicked = event.target.value;
    //props.viewRequestHandler()
    let dataLocation = null;
    switch (dayPicked) {
      case '1':
        dataLocation = 'Monday';
        break;
      case '2':
        dataLocation = 'Tuesday';
        break;
      case '3':
        dataLocation = 'Wednesday';
        break;
      case '4':
        dataLocation = 'Thursday';
        break;
      case '5':
        dataLocation = 'Friday';
        break;
      case '6':
        dataLocation = 'Saturday';
        break;
      case '7':
        dataLocation = 'Sunday';
        break;
    }
    //(event, typeOfData, handlerChoice,dataLocation,infoType, info)
    let dataRequestMessage = {
      typeOfData: 'tasks',
      handlerChoice: '9',
      dataLocation: dataLocation,
      infoType: 'pickedDayTasks',
      info: ''
    };
    tasksContext.dataRequestHandler(event, dataRequestMessage);
    let contentViewObject = {
      target: {
        value: '9'
      }
    };

    props.contentViewHandler(contentViewObject);
  };

  let displayOptions = (
    <React.Fragment>
      <div>
        <div className="btn-group-toggle d-flex-between" data-toggle="buttons">
          <label className="btn btn-danger m-2 active">
            <button
              type="radio"
              name="options"
              id="option1"
              autoComplete="off"
              onClick={props.viewContent}
              value="0"
            />{' '}
            Cockpit
          </label>

          <label className="btn btn-primary m-2 active">
            <button
              type="radio"
              name="options"
              id="option1"
              autoComplete="off"
              onClick={props.contentViewHandler}
              value="1"
            />{' '}
            Tasks For Week
          </label>

          <label className="btn btn-success m-2 active ">
            <button
              type="radio"
              name="options"
              id="option2"
              autoComplete="off"
              onClick={props.contentViewHandler}
              value="2"
            />{' '}
            Today's Tasks
          </label>

          <label className="btn btn-warning m-2 active">
            <button
              type="radio"
              name="options"
              id="option3"
              autoComplete="off"
              onClick={event => pickedSyllabusRequestHandler(event)}
              value="3"
            >
              {' '}
              View Syllabi
            </button>

            {syllabusContext.everythingSyllabus.syllabusData ? (
              <div className="dropdown-menu" aria-labelledby="dropdownMenu2">
                {processSyllabiList()}
              </div>
            ) : null}
          </label>

          <label className="btn  dropdown m-2 active">
            <button
              className="btn btn-success dropdown-toggle"
              type="button"
              id="dropdownMenu2"
              data-toggle="dropdown"
              aria-haspopup="true"
              aria-expanded="false"
            >
              Tasks by Day
            </button>

            <div className="dropdown-menu" aria-labelledby="dropdownMenu2">
              <button
                className="dropdown-item"
                type="radio"
                name="options"
                id="option1"
                autoComplete="off"
                onClick={event => pickedDayRequestHandler(event)}
                value="1"
              >
                {' '}
                Mon
              </button>

              <button
                className="dropdown-item"
                type="button"
                name="options"
                id="option1"
                autoComplete="off"
                onClick={event => pickedDayRequestHandler(event)}
                value="2"
              >
                {' '}
                Tue
              </button>

              <button
                className="dropdown-item"
                type="button"
                name="options"
                id="option1"
                autoComplete="off"
                onClick={event => pickedDayRequestHandler(event)}
                value="3"
              >
                {' '}
                Wed
              </button>
              <button
                className="dropdown-item"
                type="button"
                name="options"
                id="option1"
                autoComplete="off"
                onClick={event => pickedDayRequestHandler(event)}
                value="4"
              >
                {' '}
                Thur
              </button>
              <button
                className="dropdown-item"
                type="button"
                name="options"
                id="option1"
                autoComplete="off"
                onClick={event => pickedDayRequestHandler(event)}
                value="5"
              >
                {' '}
                Fri
              </button>
              <button
                className="dropdown-item"
                type="button"
                name="options"
                id="option1"
                autoComplete="off"
                onClick={event => pickedDayRequestHandler(event)}
                value="6"
              >
                {' '}
                Sat
              </button>
              <button
                className="dropdown-item"
                type="button"
                name="options"
                id="option1"
                autoComplete="off"
                onClick={event => pickedDayRequestHandler(event)}
                value="7"
              >
                {' '}
                Sun
              </button>
            </div>
          </label>
        </div>
      </div>
    </React.Fragment>
  );
  //<div class=" "></div></div>

  return displayOptions;
};

export default ViewContentOptions;
