import React from 'react'
import taskStyle from './Task.module.css'
// import './Task.css'
// import Radium from 'radium';
import wtf from '../SpecialBtn.module.css'

const task = (props) => {

// const querrystyle = {

// }
    const inputStyle = {
        ':focus' : { 
          backgroundColor: 'yellow'
        }
    }
  /*
const hiddenInput = {
    display: 'none',
    backgroundColor: 'yellow',
    padding: '20px',

    }

const showHiddenInput = { 
    ':hover': {
        display: 'block',
        color: 'purple'
        }
    }
*/


//onmouseenter = 'props.mouseover'
    return (
        <div className = {taskStyle.Task}>
        <div className = {taskStyle.TaskDeadlineSep}>
            <p className = {taskStyle.TaskDeadline} >Task: {props.todo}</p>
            <p className = {taskStyle.TaskDeadline} >Deadline:  {props.deadline}</p>
            <div className = {wtf.showTaskDetails2}> Hover to show Location
            <p className = {wtf.showTaskDetails}>{props.location}</p>
            </div>
            <p className = {taskStyle.TaskDeadline} onClick = {props.click}>Delete Task</p>   
        </div>


        <p>{props.children}</p>

        <input style = {inputStyle} type= 'text' onChange = {props.changed} value={props.todo}/>
        </div>
    )
    }
//export default Radium(task)
export default task
