import React from 'react';
//import useForm from 'react-hook-form';
import { tsPropertySignature } from '@babel/types';

const newTask = props => {
  //console.log(props.newTaskTitle);
  //{props.newTaskTitle? ({props.newTaskTitle}): null}

  //const { handleSubmit } = useForm();
  // const onSubmit = values => {
  //   console.log(values);
  // };

  return (
    <React.Fragment>
      <div class="container">
        <form onSubmit={props.newTaskInfo}>
          <div className="form-group">
            <label for="newTask">Task</label>
            <input
              className="form-control form-control-sm"
              name="newTask"
              type="text"
              id="company"
              placeholder="Enter new task."
              onChange={props.changed}
              value={props.newTaskTitle}
            />
          </div>

          <div className="form-group">
            <label for="category">Location</label>
            <select
              value={props.location}
              className="form-control"
              id="location"
              onChange={props.selectionChange}
            >
              <option value="Poplado">Poplado</option>
              <option value="Laureles">Laureles</option>
              <option value="Sabaneta">Sabaneta</option>
            </select>
          </div>

          <div className="form-group">
            <label for="deadline">Deadline</label>
            <textarea
              className="form-control"
              id="deadline"
              rows="3"
              value={props.deadline}
              onChange={props.locationChange}
            ></textarea>
          </div>

          <input type="submit" value="Submit" />
        </form>
      </div>
      <p>{props.newTaskTitle}</p>
      <p>{props.category}</p>
      <p>{props.location}</p>
    </React.Fragment>
  );
};

export default newTask;

