import React, { Component } from 'react';
import DailyTimeBudget from '../DailyTimeBudget/DailyTimeBudget';
import classes from './WeeklyTimeBudget.module.css';

//hygiene, traveling, packing,wating

//each day have a predetermined amt of time for each category
//then each day you can balance it according to 24 hours
//after balancing for each day, you have to go back to balance the weekly total

//24 hours a day //broken down into .5 hour
//
class weeklyTimeBudget extends Component {
  state = {
    totalWeekHours: 168,

    hoursPerDay: 24,
    weeklyBudget: {
      sleep: 7,
      exercise: 7,
      work: 49,
      free: 7,
      errands: 14,
      getReady: 28,
      totalHours: 168
    }
  };
  newTimeBudget = () => {
    let thisWeek = new DailyTimeBudget();
    thisWeek.timeBudgetDay();
    console.dir(thisWeek.dailyBudget);
    //eachCategory: keywords
    let MondayWork = { ...thisWeek.dailyBudget.Monday };
    //alert(JSON.stringify(MondayWork.work));
    console.dir(MondayWork.work);
    this.state({ weeklyTimeBudget: thisWeek });
  };

  addHourToActivity = activity => {
    const oldHourCount = this.state.totalWeekHours;
    const updatedHourCount = oldHourCount + 1;

    //upgrade the ingredients count when adding more
    const updatedWeeklyBudget = {
      ...this.state.weeklyBudget
    };

    updatedWeeklyBudget[activity] = updatedHourCount;

    // //cost for 1 new ingredient aded
    // const hourAddition = INGREDIENT_PRICES[activity];

    // //Calculate the new price after new ingredient added
    // const oldTotalWeekHours= this.state.totalWeekHours;
    // const newPrice = oldPrice + priceAddition;

    //upgrade the ingredients count & total price when adding more
    this.setState({ ingredients: updatedHourCount }); //ingredients now contains updated unit # of ingrediant just added
    //pass in the new ingredients list so that it can calculate the total price

    //this.updatePurchaseState(updatedIngredients);
  };

  render() {
    this.newTimeBudget();
    return (
      <div className={classes.WeeklyTimeBudget}>
        {Object.keys(thisWeek.activityWeekCategories).map(eachCategory => (
          <DailyTimeBudget
            key={eachCategory.concat(eachCategory)}
            activity={eachCategory}
            hours={this.state.weeklyBudget[eachCategory]}
            decrease={this.deductHourToActivity}
            add={this.addHourToActivity}
          />
        ))}
      </div>
    );
  }
}

export default weeklyTimeBudget;

