import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
// import moment from 'react-moment';
import { compareAsc, format } from 'date-fns';

const DatePickerComp = props => {
  const [startDate, setStartDate] = useState(new Date());

  const [finishTime, setFinishTime] = useState(new Date());
  //let Sunday = moment(startDate).format('MMMM Do YYYY, h:mm:ss a');

  let Sunday = format(startDate, 'MM/dd/yyyy, hh:mm:ss a');
  console.log(JSON.stringify(Sunday));

  const startDateTimeHandler = date => {
    setStartDate(date);
    props.startDateTimeHandler();
    console.log(`startDateTime: ${startDate}`);
  };

  const finishTimeHandler = date => {
    setFinishTime(date);
    //console.log(`finishDateTime: ${finishTime}`);
    props.finishTimeHandler(finishTime);
  };

  // {
  //   "name": "State",
  //   "value": "Wed Jan 08 2020 19:45:00 GMT-0500 (Colombia Standard Time)",
  //   "subHooks": []
  // }

  const startDateTime = (
    <DatePicker
      selected={startDate}
      onChange={date => startDateTimeHandler(date)}
      locale="pt-BR"
      showTimeSelect
      timeFormat="p"
      timeIntervals={15}
      dateFormat="Pp"
    />
  );
  const endTime = (
    <DatePicker
      selected={finishTime}
      onChange={date => finishTimeHandler(date)}
      showTimeSelect
      showTimeSelectOnly
      timeIntervals={15}
      timeCaption="Time"
      dateFormat="h:mm aa"
    />
  );

  return (
    <React.Fragment>
      <div>{finishTime ? <div>{finishTime}</div> : null}</div>
      <div>
        {startDateTime}
        {endTime}
      </div>
    </React.Fragment>
  );
};

