import React, { Component } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { compareAsc, format } from 'date-fns';
import { da } from 'date-fns/locale';

class DatePickerPicker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      //startDate: new Date(),
      showDate1: false,
      showDate2: false
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleStartTimeDateChange(date) {
    console.log(`date.toLocaleDateString: ${date.toLocaleDateString()}`);
    let date2 = date.toString();
    console.log(date2);
    console.log(typeof date2);
    let currenShowDate1 = this.state.startDate;
    let startTimeDate = {
      date: date,
      dateObjectString: date.toString(),
      toISOString: date.toISOString(),
      toUTCString: date.toUTCString(),
      toGMTString: date.toUTCString(),
      dateString: date.toDateString(),
      timeString: date.toTimeString(),
      year: date.getFullYear(),
      yearShort: date.getYear(),
      month: date.getMonth(),
      date: date.getDate(),
      day: date.getDay(),
      time: date.toLocaleTimeString(),
      hour: date.getHours(),
      minute: date.getMinutes(),
      UTCDate: date.getUTCDate(),
      toLocalString: date.toLocaleString(),
      toLocalDateString: date.toLocaleDateString(),
      toLocaleTimeString: date.toLocaleTimeString()
    };
    let toLocaleString = date.toLocaleString();
    this.setState({
      showDate1: !currenShowDate1,
      startDate: date,
      startTimeDate: startTimeDate
    });
  }

  handleFinishTimeDateChange(date) {
    let currenShowDate2 = this.state.showDate2;
    let finishTimeDate = {
      date: date,
      dateObjectString: date.toString(),
      toISOString: date.toISOString(),
      toUTCString: date.toUTCString(),
      toGMTString: date.toUTCString(),
      dateString: date.toDateString(),
      timeString: date.toTimeString(),
      year: date.getFullYear(),
      yearShort: date.getYear(),
      month: date.getMonth(),
      date: date.getDate(),
      day: date.getDay(),
      time: date.toLocaleTimeString(),
      hour: date.getHours(),
      minute: date.getMinutes(),
      UTCDate: date.getUTCDate(),
      toLocalString: date.toLocaleString(),
      toLocalDateString: date.toLocaleDateString(),
      toLocaleTimeString: date.toLocaleTimeString()
    };
    let toLocaleString = date.toLocaleString();
    this.setState(
      {
        finishTimeDate: finishTimeDate,
        finishDate: date,
        showDate2: !currenShowDate2
      },
      () => this.props.finishTimeHandler(this.state.finishTimeDate)
    );
    console.log(`inside handleChange2 datePicker ${this.state.startDate}`);
  }

  handleSubmit(e) {
    e.preventDefault();
    let main = this.state.startDate;
    alert(main);
  }

  render() {
    return (
      <React.Fragment>
        <div className="container">
          <form onSubmit={this.handleSubmit}>
            <div className="form-group">
              <label>Select Event Date & Time: </label>

              <DatePicker
                placeholderText="Choose Start Time"
                selected={this.state.startDate}
                onChange={date => this.handleStartTimeDateChange(date)}
                showTimeSelect
                timeFormat="HH:mm"
                timeIntervals={15}
                timeCaption="Start"
                dateFormat="MMMM d, yyyy h:mm aa"
              />

              <DatePicker
                placeholderText="Choose End Time"
                selected={this.state.finishDate}
                onChange={date => this.handleFinishTimeDateChange(date)}
                showTimeSelect
                timeFormat="HH:mm"
                timeIntervals={15}
                timeCaption="End"
                dateFormat="MMMM d, yyyy h:mm aa"
              />
            </div>
            <div className="form-group">
              <button className="btn btn-success">Add Date</button>
            </div>
          </form>
        </div>
      </React.Fragment>
    );
  }
}

export default DatePickerPicker;
