import React, { Component } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { compareAsc, format } from 'date-fns';

class DatePickerPicker extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startDate: new Date()
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(date) {
    this.setState(
      {
        startDate: date
      },
      () => this.props.startDateTimeHandler(this.state.startDate)
    );
    console.log(`inside handleChange datePicker ${this.state.startDate}`);
  }

  handleSubmit(e) {
    e.preventDefault();
    let main = this.state.startDate;
    alert(main);
    //console.log(main.format('L'));
  }

  render() {
    return (
      <div className="container">
        <h3>React Datepicker Example</h3>

        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label>Select Date: </label>
            <DatePicker
              selected={this.state.startDate}
              onChange={this.handleChange}
              name="startDate"
              //dateFormat="MM/dd/yyyy"
              dateFormat="PP"
              //locale="p-BR"
              showTimeSelect
              timeFormat="p"
              timeIntervals={15}
              timeCaption="Start"
            />

            <DatePicker
              selected={startDate}
              onChange={date => setStartDate(date)}
              showTimeSelect
              timeFormat="HH:mm"
              timeIntervals={15}
              timeCaption="time"
              dateFormat="MMMM d, yyyy h:mm aa"
            />
          </div>
          <div className="form-group">
            <button className="btn btn-success">Add Date</button>
          </div>
        </form>
      </div>
    );
  }
}

export default DatePickerPicker;
