import React, { Component } from 'react';
import rocky from './App.module.css';
import Tasks from '../components/ViewContent/Tasks/Tasks';

import Cockpit from '../components/Cockpit/Cockpit';
import classes from '../components/Cockpit/Cockpit.module.css';

import WithClass from '../hoc/WithClass';

import RightCockpit from './RightCockpit/RightCockpit';

//For react Scheduler
import Scheduler, {
  SchedulerData,
  ViewTypes,
  DATE_FORMAT
} from 'react-big-scheduler';
//include `react-big-scheduler/lib/css/style.css` for styles, link it in html or import it here
import 'react-big-scheduler/lib/css/style.css';
import moment from 'moment';

// var CanvasJSReact = require('./canvasjs.react');
// var CanvasJS = CanvasJSReact.CanvasJS;
// var CanvasJSChart = CanvasJSReact.CanvasJSChart;

//import Student from './Student.css';
//import StudentTable from './StudentTable'
// import StudentTable from './StudentTable';
//import Navbar from '../components/Cockpit/navBar';
//import Syllabus from '../components/Syllabus/Syllabus';

//import for bootstraps
// import axios from 'axios';
// import { Container, Row, Col } from 'reactstrap';
// import Post from '../components/Post';
// import Header from '../components/Header';
// import SideCard from '../components/SideCard';

import TasksData from './Store/TasksData';
import SyllabusData from './Store/SyllabusData';
import Store from './Store/Store';

class App extends Component {
  constructor(props) {
    super(props);
    let schedulerData = new SchedulerData(
      new moment().format(DATE_FORMAT),
      ViewTypes.Week
    );
    moment.locale('zh-cn');
    schedulerData.setLocaleMoment(moment);

    let resources = [
      {
        id: 'r0',
        name: 'Resource0',
        groupOnly: true
      },
      {
        id: 'r1',
        name: 'Resource1'
      },
      {
        id: 'r2',
        name: 'Resource2',
        parentId: 'r0'
      },
      {
        id: 'r3',
        name: 'Resource3',
        parentId: 'r4'
      },
      {
        id: 'r4',
        name: 'Resource4',
        parentId: 'r2'
      }
    ];
    schedulerData.setResources(resources);
    let events = [
      {
        id: 1,
        start: '2017-12-18 09:30:00',
        end: '2017-12-19 23:30:00',
        resourceId: 'r1',
        title: 'I am finished',
        bgColor: '#D9D9D9'
      },
      {
        id: 2,
        start: '2017-12-18 12:30:00',
        end: '2017-12-26 23:30:00',
        resourceId: 'r2',
        title: 'I am not resizable',
        resizable: false
      },
      {
        id: 3,
        start: '2017-12-19 12:30:00',
        end: '2017-12-20 23:30:00',
        resourceId: 'r3',
        title: 'I am not movable',
        movable: false
      },
      {
        id: 4,
        start: '2017-12-19 14:30:00',
        end: '2017-12-20 23:30:00',
        resourceId: 'r1',
        title: 'I am not start-resizable',
        startResizable: false
      },
      {
        id: 5,
        start: '2017-12-19 15:30:00',
        end: '2017-12-20 23:30:00',
        resourceId: 'r2',
        title: 'R2 has recurring tasks every week on Tuesday, Friday',
        rrule: 'FREQ=WEEKLY;DTSTART=20171219T013000Z;BYDAY=TU,FR',
        bgColor: '#f759ab'
      }
    ];
    schedulerData.setEvents(events);
  }

  //
  //React Scheduler

  render() {
    return (
      <WithClass passClass={rocky.App}>
        <Scheduler
          schedulerData={schedulerData}
          prevClick={this.prevClick}
          nextClick={this.nextClick}
          onSelectDate={this.onSelectDate}
          onViewChange={this.onViewChange}
          eventItemClick={this.eventClicked}
        />
        <Store />
      </WithClass>
    );
  }
}
//<p>{this.props.appTitle}</p>
export default App;
