import React from 'react'
import './Task.css'
import Radium from 'radium';

const task = (props) => {
//     const inputStyle = {
//         ':focus' : { 
//           backgroundColor: 'yellow'
//   }

const hiddenInput = {
    display: 'none',
    backgroundColor: 'yellow',
    padding: '20px',

    }

const showHiddenInput = { 
    ':hover': {
        display: 'block',
        color: 'purple'
        }
    }


    return (
        <div className = 'Task'>
        <div className = 'TaskDeadlineSep'>
            <p className = 'TaskDeadline' >Task: {props.todo}</p>
            <p className = 'TaskDeadline' >Deadline:  {props.deadline}</p>    
            <p className = 'TaskDeadline delete' onClick = {props.click}>Delete Task</p>
        </div>


        <p style = {showHiddenInput}>{props.children}</p>

        <input style = {hiddenInput} type= 'text' onChange = {props.changed} value={props.todo}/>
        </div>
    )
    }
export default Radium(task)
