import React from 'react'
import './Task.css'
import Radium from 'radium';
import { NONAME } from 'dns';

const task = (props) => {
//     const inputStyle = {
//         ':focus' : { 
//           backgroundColor: 'yellow'
//   }

const inputStyle = {
    display: 'none',
    backgroundColor: 'yellow',
    padding: '20px',
    }

    const showTaskInput = {
        ':hover': {
            display: 'block',
          }
        }


    return (
        <div className = 'Task'>
        <div className = 'TaskDeadlineSep'>
            <p className = 'TaskDeadline' >Task: {props.todo}</p>
            <p className = 'TaskDeadline' >Deadline:  {props.deadline}</p>    
            <p className = 'TaskDeadline delete' onClick = {props.click}>Delete Task</p>
        </div>


        <p style = {showTaskInput}>{props.children}</p>

        <input style = {inputStyle} type= 'text' onChange = {props.changed} value={props.todo}/>
        </div>
    )
    }
export default Radium(task)
