// const [newEvent, setNewEvent] = useState({
//   title: ''
// });

// setNewEvent({
//   [e.target.name]: e.target.value
// });
// let bigEvent = { [e.target.name]: e.target.value };
// props.newestEvent(bigEvent);
// };

class NewEvent extends Component {
  state = {
    eventTitle: '',
    day: '',
    month: '',
    year: '',
    time: '',
    task: {
      eventId: '', //task + date + start time
      blockOffTimeSlot: '', //T-F  when event will take on time slot for scheduled day
      eventTitle: '', //title, string
      note: '', //text, string
      deadline: '', //single date, absolute deadline
      category: '', //errand,multiday event, single day event,
      source: '', //event
      assignedTimeStart: '', //00:00 - 24:00, string
      assignedTimeStop: '', //00:00 - 24:00, string
      assignedDateStart: '',
      assignedDateStop: ''
    },
    date: null,
    focused: null,
    showPickedDate: false
  };
  getPickerValue = value => {
    console.log(value);
  };
  eventTitleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  eventNoteChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    this.props.newestEvent(this.state);
  };
  handleDateChange = date => {
    //let date = event.target.value;
    this.setState({ date: date, showPickedDate: true });
  };

  startDateTimeHandler = date => {
    this.setState({ startDate1: date });
    //alert(this.state.date._d)

    console.log(`Inside NewEvent startDateTimeHandler: ${date} `);
  };

  finishTimeHandler = date => {
    this.setState({ startDate2: date });
    console.log(`finishTimeHandler in newEvent: ${date}`);
  };

  render() {
    return (
      <React.Fragment>
        <div className="container">
          <form>
            <div className="form-group">
              <label>Event Title</label>
              <input
                type="text"
                name="task.eventTitle"
                className="form-control"
                value={this.state.task.task}
                onChange={e => this.eventTitleChange(e)}
              />
            </div>

            <div className="form-group">
              <label>Note</label>
              <input
                type="text"
                name="task.note"
                className="form-control"
                value={this.state.task.note}
                onChange={e => this.eventNoteChange(e)}
              />

              <DatePickerPicker
                startDateTimeHandler={date => this.startDateTimeHandler(date)}
                finishTimeHandler={date => this.finishTimeHandler(date)}
              />
            </div>

            <button onClick={e => this.onSubmit(e)}>Submit</button>
          </form>
        </div>
      </React.Fragment>
    );
  }
}
// {this.state.showPickedDate ? { showCurrentPickedDate } : null}
export default NewEvent;

